import React from 'react';
import { Card,  CardMedia, Button, Typography } from '@material-ui/core/';
import DeleteIcon from '@material-ui/icons/Delete';
import { useDispatch } from 'react-redux';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import {  deletePost } from '../../../actions/posts';
import useStyles from './styles';
import EditIcon from '@mui/icons-material/Edit';
const Post = ({ post, setCurrentId }) => {
  const dispatch = useDispatch();


  return (
[!post.creator, !post.tag , !post.message ] ?  !post
:
    <Card sx={{ maxWidth: 345 }}>
    <div
     style={{
 display:'flex',
 justifyContent:'center',

    }}>
  <CardMedia
  image={post.selectedFile || 'https://user-images.githubusercontent.com/194400/49531010-48dad180-f8b1-11e8-8d89-1e61320e1d82.png'} 
    component="img"
    height="140"

    alt="green iguana"
    style={{
        height:'200px',
        width:'200px',
        borderRadius:"50%",


    }}
     />
  </div>
  <CardContent style={{
      textAlign:'start',
      marginLeft:'40px',
      textTransform:'capitalize'
  }}>
 <Typography variant="h4">{post.creator}</Typography>
 <Typography variant="h5" color="textSecondary" component="p">{post.message}</Typography>
    <Typography gutterBottom variant="h5" component="h2">{post.title}</Typography>
    <Typography variant="h5" color="textSecondary" component="h2">{post.tags.map((tag) => `$${tag} `)}</Typography>
  </CardContent>
  <CardActions style={{display:'flex', justifyContent:'space-between'}}>
  <Button style={{ color: 'blue' }} size="medium" color="secondary" onClick={() => dispatch(deletePost(post._id))}><DeleteIcon fontSize="small" /></Button>
  <Button style={{ color: 'blue' }} size="small" onClick={() => setCurrentId(post._id)}><EditIcon fontSize="default" /></Button>
  </CardActions>
</Card>

  );
};

export default Post;
